import { Calendar, momentLocalizer, Event } from "react-big-calendar";
import moment from "moment";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";

import "react-big-calendar/lib/addons/dragAndDrop/styles.css";
import "react-big-calendar/lib/css/react-big-calendar.css";

const localizer = momentLocalizer(moment);

const events: Event[] = [
  {
    title: "test event",
    start: moment("20220110", "YYYYMMDD").toDate(),
    end: moment("20220116", "YYYYMMDD").toDate(),
    owner: "Jack",
  },
  {
    title: "Weekend with team",
    start: moment("20220127", "YYYYMMDD").toDate(),
    end: moment("20220131", "YYYYMMDD").toDate(),
    owner: "M & D",
  },
];

const barColor = {
  Jack: "blue",
  Charlie: "red",
  Lily: "pink",
  "M & D": "green",
  Other: "grey",
};

export const CalendarCard = () => (
  <Box
    sx={{
      width: "95vw",
      margin: "auto",
      opacity: 0.92,
      marginTop: "5vh",
    }}
  >
    <Card>
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        views={["month"]}
        style={{ height: 500, width: "60vw", padding: "25px" }}
        eventPropGetter={(event) => {
          let newStyle = {
            backgroundColor: "lightgrey",
            color: "black",
            borderRadius: "0px",
            border: "none",
          };
          newStyle.backgroundColor = barColor[event.owner];
          return {
            className: "",
            style: newStyle,
          };
        }}
      />
    </Card>
  </Box>
);
