import { Calendar, momentLocalizer, Event } from "react-big-calendar";
import moment from "moment";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";

import "react-big-calendar/lib/addons/dragAndDrop/styles.css";
import "react-big-calendar/lib/css/react-big-calendar.css";

const localizer = momentLocalizer(moment);

const events: Event[] = [
  {
    title: "test event",
    start: moment("20220110", "YYYYMMDD").toDate(),
    end: moment("20220116", "YYYYMMDD").toDate(),
    owner: "Jack",
  },
  {
    title: "Weekend with team",
    start: moment("20220127", "YYYYMMDD").toDate(),
    end: moment("20220131", "YYYYMMDD").toDate(),
    owner: "M & D",
  },
];

const barColor = {
  Jack: {
    background: "#8BBBCC",
    border: "thin solid #3082A0",
    text: "#3082A0",
  },
  Charlie: {
    background: "blue",
    border: "thin solid red",
    text: "#3082A0",
  },
  Lily: {
    background: "blue",
    border: "thin solid red",
    text: "#3082A0",
  },
  "M & D": {
    background: "blue",
    border: "thin solid red",
    text: "#3082A0",
  },
  Other: {
    background: "blue",
    border: "thin solid red",
    text: "#3082A0",
  },
};

export const CalendarCard = () => (
  <Box
    sx={{
      width: "95vw",
      margin: "auto",
      opacity: 0.92,
      marginTop: "5vh",
    }}
  >
    <Card>
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        views={["month"]}
        style={{ height: 500, width: "95vw", padding: "25px", margin: "auto" }}
        eventPropGetter={(event) => {
          let newStyle = {
            backgroundColor: "lightgrey",
            border: "thin solid grey",
            color: "white",
          };
          newStyle.backgroundColor = barColor[event.owner].background;
          newStyle.border = barColor[event.owner].border;
          return {
            className: "",
            style: newStyle,
          };
        }}
      />
    </Card>
  </Box>
);
